      writer_id: "0a442720-2707-4e3e-b5cc-9c65b806bd1c",
      coordinator_id: 3,
      winner_number: 2,
      payload: {
        id: 43,
        name: title,
        description: description,
        type: "NFT",
        data: {
          url: url,
        },
      },
    };
  };
  const handleChange = (e) => {
    setCorrectAnswer(e.target.value);
  };
  const validate = () => {
    let isValid = true;
    const le = { title: "", description: "", url: "" };
    if (title.length <= 0) {
      le["title"] = " can't be empty";
      isValid = false;
    }

    if (description.length < 6) {
      le["description"] = " should be at least 6 in length";
      isValid = false;
    }
    if (url.length < 5) {
      le["url"] = " should be at least 5 in length";
      isValid = false;
    }

    setErrors(le);
    return isValid;
  };

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    const isAuth = true;
    if (user == null) {
      setUserIsAuth(false);
      navigate("/user/login");
    }
    const fetchData = async () => {
      console.log("Here ");
    };
    fetchData();
  }, []);

  const handleOnSubmit = async (e) => {
    // Send to server
    e.preventDefault();
    console.log("Value ", correctAnswer);
    // If error then we don't dispatch
    //   if (!validate(title, description, url)) return;
    let correct1 = false;
    let correct2 = false;
    let correct3 = false;
    let correct4 = false;

    if (correctAnswer == "answer_1") {
      correct1 = true;
    } else if (correctAnswer == "answer_2") {
      correct2 = true;
    } else if (correctAnswer == "answer_3") {
      correct3 = true;
    } else {
      correct4 = true;
    }

    const user = JSON.parse(localStorage.getItem("user"));
    console.log("The user ", user, "id ", user.id);
    console.log("The user right here on submit ", user);

    let requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      credentials: "include",
      body: JSON.stringify({
        title: title,
        titleImage: url,
        owner: user,
      }),
    };
    let getRequest = {
      method: "GET",
      headers: { "Content-Type": "application/json" },
    };
    let userRes = await fetch("http://localhost:4567/user/info", getRequest);
    console.log("user res ", userRes);
  };

  const createBlock = () => async (dispatch, getState) => {
    console.log("Inside get matches ");
    const requestOptions = {
      method: "GET",
      headers: { "Content-Type": "application/json" },
      credentials: "include",
    };
    const data = await fetch("http://localhost:4567/matches", requestOptions);
    if (!data.ok) {
      console.log("Lol you failed");
    } else {
      return await data.json();
    }
  };

  const createNFT = () => (async) => {
    console.log("get dict  ", getDict());
