          {errors.title}
          <input
            type="text"
            value={title}
            title="title"
            onChange={(e) => settitle(e.target.value)}
          />
        </label>

        <label>
          IPSF URL
          {errors.url}
          <input
            type="text"
            value={url}
            title="url"
            onChange={(e) => setUrl(e.target.value)}
          />
        </label>
        <label>
          Description
          {errors.description}
          <input
            type="text"
            value={description}
            title="description"
            onChange={(e) => setdescription(e.target.value)}
          />
        </label>

        <button type="submit" onClick={(e) => createNFT(e.target.value)}>
          Create
        </button>
      </form>
      <div>
        <div id="left-side">
          <div id="profile-pic">
            {" "}
            <img src={url} id="profilepic" onError={altImage} />{" "}
          </div>
          <div id="profile-name">{title} </div>
          <div id="profile-username"> {description} </div>
        </div>
      </div>
    </div>
  );
};
export default NodeSystem;
