import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { Link, useNavigate } from "react-router-dom";

const NodeSystem = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate;
  const [title, settitle] = useState("");
  const [description, setdescription] = useState("");
  const [imageURL, setimageURL] = useState("");
  const [answer1, setAnswer1] = useState("");
  const [answer2, setAnswer2] = useState("");
  const [answer3, setAnswer3] = useState("");
  const [answer4, setAnswer4] = useState("");
  const [answer1Correct, setAnswer1Correct] = useState(false);
  const [answer2Correct, setAnswer2Correct] = useState(false);
  const [answer3Correct, setAnswer3Correct] = useState(false);
  const [answer4Correct, setAnswer4Correct] = useState(false);
  const [errors, setErrors] = useState({
    title: "",
    description: "",
    imageURL: "",
  });
  const [correctAnswer, setCorrectAnswer] = useState("answer_1");
  const [userIsAuth, setUserIsAuth] = useState(false);
  const [userInfo, setUserInfo] = useState("");
  const altImage = "https://static.thenounproject.com/png/396915-200.png";
  const getDict = () => {
    return {
      previous_hash: "4d3e9b1e-816f-473b-8bd2-dd642681f07d",
      writer_signature: "0a442720-2707-4e3e-b5cc-9c65b806bd1c",
      writer_id: "0a442720-2707-4e3e-b5cc-9c65b806bd1c",
      coordinator_id: 3,
      winner_number: 2,
      payload: {
        id: 43,
        name: title,
        description: description,
        type: "NFT",
        data: {
          imageURL: imageURL,
        },
      },
    };
  };
  const handleChange = (e) => {
    setCorrectAnswer(e.target.value);
  };
  const validate = () => {
    let isValid = true;
    const le = { title: "", description: "", imageURL: "" };
    if (title.length <= 0) {
      le["title"] = " can't be empty";
      isValid = false;
    }

    if (description.length < 6) {
      le["description"] = " should be at least 6 in length";
      isValid = false;
    }
    if (imageURL.length < 5) {
      le["imageURL"] = " should be at least 5 in length";
      isValid = false;
    }

    setErrors(le);
    return isValid;
  };

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    const isAuth = true;

    const fetchData = async () => {
      console.log("Here ");
    };
    fetchData();
  }, []);

  const handleOnSubmit = async (e) => {
    // Send to server
    e.preventDefault();
    console.log("Value ", correctAnswer);
    // If error then we don't dispatch
    //   if (!validate(title, description, imageURL)) return;
    let correct1 = false;
    let correct2 = false;
    let correct3 = false;
    let correct4 = false;

    if (correctAnswer == "answer_1") {
      correct1 = true;
    } else if (correctAnswer == "answer_2") {
      correct2 = true;
    } else if (correctAnswer == "answer_3") {
      correct3 = true;
    } else {
      correct4 = true;
    }
  };

  const user = JSON.parse(localStorage.getItem("user"));
  console.log("The user ", user, "id ", user.id);
  console.log("The user right here on submit ", user);

  const createBlock = () => async (blockObj) => {
    console.log("Inside get matches ");
    const requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
    };
    console.log("DOing cool stuff ", blockObj);
    const data = await fetch("localhost:4000/api/blocks?key=1", requestOptions);
    if (!data.ok) {
      console.log("Lol you failed");
    } else {
      console.log("Great success");
      return await data.json();
    }
  };

  const createNFT = () => (async) => {
    console.log("get dict  ", getDict());
    const blockObj = getDict();
    dispatch(createBlock(blockObj));
  };

  return (
    <div>
      <h1>Create NFT!</h1>
      <form action="" method="get" onSubmit={(e) => handleOnSubmit(e)}>
        <label>
          Title of NFT
          {errors.title}
          <input
            type="text"
            value={title}
            title="title"
            onChange={(e) => settitle(e.target.value)}
          />
        </label>

        <label>
          IPSF imageURL
          {errors.imageURL}
          <input
            type="text"
            value={imageURL}
            title="imageURL"
            onChange={(e) => setimageURL(e.target.value)}
          />
        </label>
        <label>
          Description
          {errors.description}
          <input
            type="text"
            value={description}
            title="description"
            onChange={(e) => setdescription(e.target.value)}
          />
        </label>

        <button type="submit" onClick={(e) => createNFT(e.target.value)}>
          Create
        </button>
      </form>
      <div>
        <div id="left-side">
          <div id="profile-pic">
            {" "}
            <img src={imageURL} id="profilepic" />{" "}
          </div>
          <div id="profile-name">{title} </div>
          <div id="profile-username"> {description} </div>
        </div>
      </div>
    </div>
  );
};
export default NodeSystem;
